pragma solidity >=0.6.10 <0.7.0;
import "@openzeppelin/contracts/proxy/Initializable.sol";

contract TimeLock is Initializable {
    IStakingAuRa stakingContract;
    IResolver resolverContract;
    address owner;
    address defaultPool;
    uint256 releaseTime;
    bool forwardOnReceive;
    uint256 constant MIN_DELEGATOR_STAKE = 10000000000000000000000;
    uint256 constant VEST_STEP = 2629743;
    uint256 constant LAST_VEST_PERIOD = 6;

    uint256 lockThreshold;
    uint256 stakeSum;

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    function initialize(
        address _stakingContract,
        address _resolverContract,
        address _owner,
        address _defaultPool,
        uint _lockTime,
        uint256 _lockThreshold
    ) public initializer {
        stakingContract = IStakingAuRa(_stakingContract);
        resolverContract = IResolver(_resolverContract);
        defaultPool = _defaultPool;
        owner = _owner;
        releaseTime = now + _lockTime;
        lockThreshold = _lockThreshold;
        if (defaultPool != address(0x0)) {
            forwardOnReceive = true;
        }

        resolverContract.addLockupAddress(owner, address(this));
    }

    receive() external payable {
        if (forwardOnReceive) {
            bool allowed = stakingContract.areStakeAndWithdrawAllowed();
            uint256 stake = stakingContract.stakeAmount(defaultPool, address(this));
            if (msg.value < MIN_DELEGATOR_STAKE) {
                if (stake < MIN_DELEGATOR_STAKE) {
                    allowed = false;
                }
            }
            if (allowed) {
                stakingContract.stake.value(msg.value)(defaultPool, msg.value);
                stakeSum += msg.value;
                // check if it was first stake and disable
                if (stake == 0) {
                    forwardOnReceive = false;
                }
            }
        }
    }

    function getOwner() external view returns (address x) {
        return owner;
    }

    function getDefaultPool() external view returns (address x) {
        return defaultPool;
    }

    function enableForwarding() external onlyOwner {
        forwardOnReceive = true;
    }

    function disableForwarding() external onlyOwner {
        forwardOnReceive = false;
    }

    function isForwardingEnabled() external view returns (bool x) {
        return forwardOnReceive;
    }

    function withdraw() external onlyOwner {
        // check if user has funds due for pay out because lock time is over
        require(address(this).balance != 0);

        uint256 max = maxWithdrawAllowed();
        require(max > 0);

        msg.sender.transfer(max);
    }

    function getTotalStaked() external view returns (uint256 x) {
        return stakeSum;
    }

    function getNonStakedFunds() external view returns (uint x) {
        return address(this).balance;
    }

    function getLockedFundsReleaseTime() external view returns (uint x) {
        return releaseTime;
    }

    function maxWithdrawAllowed() public view returns (uint256 x) {
        uint256 totalAmount = address(this).balance;
        if (releaseTime < now) {
            return totalAmount;
        }

        uint256 totalLocked = lockThreshold - stakeSum;
        uint256 remainingMonths = (releaseTime - now) / VEST_STEP ;
        if (remainingMonths >= LAST_VEST_PERIOD) {
            if (totalAmount > totalLocked) {
                return totalAmount - totalLocked;
            }
            return 0;
        }

        uint256 unlocked = (LAST_VEST_PERIOD - remainingMonths) / LAST_VEST_PERIOD * lockThreshold;
        if (totalLocked > unlocked){
        totalLocked -= unlocked;
        }

        return totalAmount - totalLocked;
    }

    function stake(address _pool, uint256 _amount) external onlyOwner {
        require(address(this).balance >= _amount);
        uint256 stake = stakingContract.stakeAmount(_pool, address(this));
        stakingContract.stake.value(_amount)(_pool, _amount);
        stakeSum += _amount;
        // check if it was first stake and disable
        if (stake == 0) {
            forwardOnReceive = false;
        }
    }

    function unstake(address _pool, uint256 _amount) external onlyOwner {
        bool forward = forwardOnReceive;
        forwardOnReceive = false;

        stakingContract.withdraw(_pool, _amount);
        stakeSum -= _amount;
        forwardOnReceive = forward;
    }

    function changeDefaultPool(address _newPool) external onlyOwner {
        defaultPool = _newPool;
    }

    function stakeAmount(address _pool) external view returns(uint256) {
        uint256 amount = stakingContract.stakeAmount(_pool, address(this));
        return amount;
    }

    function requestUnstake(address _pool, uint256 _amount) external onlyOwner {
        stakingContract.orderWithdraw(_pool, int256(_amount));
    }

    function claimUnstakeRequest(address _pool) external onlyOwner {
        stakingContract.claimOrderedWithdraw(_pool);
    }
}

interface IResolver {
    function addLockupAddress(address _account, address _lockup) external;
}

interface IStakingAuRa {
    function stake(address _toPoolStakingAddress, uint256 _amount) external payable;
    function withdraw(address _fromPoolStakingAddress, uint256 _amount) external;
    function stakeAmount(address, address) external view returns(uint256);
    function areStakeAndWithdrawAllowed() external view returns(bool);
    function orderWithdraw(address _poolStakingAddress, int256 _amount) external;
    function claimOrderedWithdraw(address _poolStakingAddress) external;
}
